<span class="line"><span style="color:#ABB2BF;">    | expression (</span><span style="color:#98C379;">&#39;+&#39;</span><span style="color:#ABB2BF;"> | </span><span style="color:#98C379;">&#39;-&#39;</span><span style="color:#ABB2BF;">) expression</span></span>
